// client/src/pages/HomePage.js

import React, { useEffect, useState } from 'react';
import axios from '../axios'; // Import the axios instance you created
import { Link } from 'react-router-dom'; // Import Link from react-router-dom

const HomePage = () => {
    const [transactions, setTransactions] = useState([]);

    useEffect(() => {
        const fetchTransactions = async () => {
            try {
                const response = await axios.get('/transactions'); // Use the axios instance
                setTransactions(response.data);
            } catch (error) {
                console.error('Error fetching transactions:', error);
            }
        };

        fetchTransactions();
    }, []);

    return (
        <div>
            <h1>Welcome to the Expense Tracker</h1>
            <h2>Your Transactions</h2>
            <ul>
                {transactions.map(transaction => (
                    <li key={transaction._id}>
                        {transaction.title}: ${transaction.amount} ({transaction.type})
                    </li>
                ))}
            </ul>
            {/* Navigation Buttons */}
            <Link to="/analytics">
                <button>Go to Analytics</button>
            </Link>
            <Link to="/add-transaction">
                <button>Add Transaction</button>
            </Link>
        </div>
    );
};

export default HomePage;
